import { useState } from 'react';
import hitToast from '../helpers/hitToast';
import validator from 'validator';

const API_ENDPOINT = 'http://example.com/sendemail';

export default function SubscriptionForm() {
  const [email, setEmail] = useState('');
  const [alertClass, setAlertClass] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validator.isEmail(email)) {
      setAlertClass('alert-validate');
      hitToast('Please enter a valid email address', 'error');
      return;
    }

    try {
      const res = await fetch(API_ENDPOINT, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ email })
      });

      if (res.ok) {
        const data = await res.json();
        hitToast(data.message, 'success');
      } else {
        throw new Error('Something went wrong. Please try again.');
      }
    } catch (error) {
      console.error(error);
      hitToast('Something went wrong. Please try again.', 'error');
    }

    setAlertClass('');
  }

  return (
    <form className="w-full flex-w flex-c-m validate-form" onSubmit={handleSubmit}>
      <div className={`wrap-input100 validate-input where1 ${alertClass}`} data-validate="Valid email is required: user@email.domain">
        <input className="input100 placeholder0 s2-txt2" type="text" name="email" placeholder="Enter Email Address" value={email} onChange={e => setEmail(e.target.value)} />
        <span className="focus-input100"></span>
      </div>

      <button className="flex-c-m size3 s2-txt3 how-btn1 trans-04 where1" type="submit">
        Subscribe
      </button>
    </form>
  );
}
